  "typescriptCustomType" : "class Robot {
    room: number[][];
    row: number;
    col: number;
    direction: number;
    needClean: number;
    dmap: number[][] = [[-1,0],[0,1],[1,0],[0,-1]];
    constructor(vec2d: number[][], r: number, c: number) {
        this.room = vec2d;
        this.row = r;
        this.col = c;
        this.direction = 0;
        this.needClean = 0;
        for (var i = 0; i < this.room.length; i++) {
            for (var j = 0; j < this.room[0].length; j++) {
                if (this.room[i][j] == 1) {
                    this.needClean++;
                }
            }
        }
    }

    move(): boolean {
        var r = this.row + this.dmap[this.direction][0];
        var c = this.col + this.dmap[this.direction][1];
        if (r < 0 || c < 0 || r >= this.room.length || c >= this.room[0].length || this.room[r][c] == 0) {
            return false;
        }
        this.row = r;
        this.col = c;
        return true;
    }

    turnRight() {
        this.direction = (this.direction + 1) % 4;
    }

    turnLeft() {
        this.direction = (this.direction + 3) % 4;
    }

    clean() {
        if (this.room[this.row][this.col] == 1) {
          this.room[this.row][this.col] = 2;   // mark as cleaned
          this.needClean--;
        }
    }
};"
